  async function updateStreamsList() {
    Janus.debug("updateStreamsList");
    const myInit = {
      method: 'GET'
    }; // fetch api 
    //const id = '60c07f7c360386d21c4ba0eb'; // fetch id

    const id = '60d44ebac53d31346858c759';
    const device = await fetch(`http://localhost:5003/auth/users/${id}/device`, myInit).then(response => response.json()).then(data => data.device);
    Janus.log("fetched device ", device);
    $('#update-streams').unbind('click').addClass('fa-spin');
    var body = {
      request: "list"
    };
    Janus.debug("Sending message:", body);
    streaming.send({
      message: body,
      success: function (result) {
        setTimeout(function () {
          $('#update-streams').removeClass('fa-spin').click(updateStreamsList);
        }, 500);

        if (!result) {
          bootbox.alert("Got no response to our query for available streams");
          return;
        }

        if (result["list"]) {
          $('#streams').removeClass('hide').show();
          $('#streamslist').empty();
          $('#watch').attr('disabled', true).unbind('click');
          var list = result["list"];
          Janus.log("Got a list of available streams");

          if (list && Array.isArray(list)) {
            list.sort(function (a, b) {
              if (!a || a.id < (b ? b.id : 0)) return -1;
              if (!b || b.id < (a ? a.id : 0)) return 1;
              return 0;
            });
          }

          Janus.debug(list);
          list = list.filter(stream => device.includes(stream.id + ""));
          Janus.log("filtered list : ", list);

          for (var mp in list) {
            Janus.debug("  >> [" + list[mp]["id"] + "] " + list[mp]["description"] + " (" + list[mp]["type"] + ")");
            $('#streamslist').append("<li><a href='#' id='" + list[mp]["id"] + "'>" + list[mp]["description"] + " (" + list[mp]["type"] + ")" + "</a></li>");
          }

          $('#streamslist a').unbind('click').click(function () {
            selectedStream = $(this).attr("id");
            $('#streamset').html($(this).html()).parent().removeClass('open');
            return false;
          });
          $('#watch').removeAttr('disabled').unbind('click').click(startStream);
        }
      }
    });
  }

  function getStreamInfo() {
