    $('#tl-1').removeClass('btn-primary btn-success').addClass('btn-primary').unbind('click').click(function () {
      toastr.info("Capping SVC temporal layer, wait for it... (medium FPS)", null, {
        timeOut: 2000
      });
      if (!$('#tl-2').hasClass('btn-success')) $('#tl-2').removeClass('btn-primary btn-info').addClass('btn-primary');
      $('#tl-1').removeClass('btn-primary btn-info').addClass('btn-info');
      if (!$('#tl-0').hasClass('btn-success')) $('#tl-0').removeClass('btn-primary btn-info').addClass('btn-primary');
      streaming.send({
        message: {
          request: "configure",
          temporal_layer: 1
        }
      });
    });
    $('#tl-2').removeClass('btn-primary btn-success').addClass('btn-primary').unbind('click').click(function () {
      toastr.info("Capping SVC temporal layer, wait for it... (highest FPS)", null, {
        timeOut: 2000
      });
      $('#tl-2').removeClass('btn-primary btn-info btn-success').addClass('btn-info');
      if (!$('#tl-1').hasClass('btn-success')) $('#tl-1').removeClass('btn-primary btn-info').addClass('btn-primary');
      if (!$('#tl-0').hasClass('btn-success')) $('#tl-0').removeClass('btn-primary btn-info').addClass('btn-primary');
      streaming.send({
        message: {
          request: "configure",
          temporal_layer: 2
        }
      });
    });
  }

  function updateSvcButtons(spatial, temporal) {
    // Check the spatial layer
    if (spatial === 0) {
      toastr.success("Switched SVC spatial layer! (lower resolution)", null, {
        timeOut: 2000
      });
      $('#sl-1').removeClass('btn-primary btn-success').addClass('btn-primary');
      $('#sl-0').removeClass('btn-primary btn-info btn-success').addClass('btn-success');
    } else if (spatial === 1) {
      toastr.success("Switched SVC spatial layer! (normal resolution)", null, {
        timeOut: 2000
      });
      $('#sl-1').removeClass('btn-primary btn-info btn-success').addClass('btn-success');
      $('#sl-0').removeClass('btn-primary btn-success').addClass('btn-primary');
    } // Check the temporal layer


    if (temporal === 0) {
      toastr.success("Capped SVC temporal layer! (lowest FPS)", null, {
        timeOut: 2000
      });
      $('#tl-2').removeClass('btn-primary btn-success').addClass('btn-primary');
      $('#tl-1').removeClass('btn-primary btn-success').addClass('btn-primary');
      $('#tl-0').removeClass('btn-primary btn-info btn-success').addClass('btn-success');
    } else if (temporal === 1) {
      toastr.success("Capped SVC temporal layer! (medium FPS)", null, {
        timeOut: 2000
      });
      $('#tl-2').removeClass('btn-primary btn-success').addClass('btn-primary');
      $('#tl-1').removeClass('btn-primary btn-info btn-success').addClass('btn-success');
      $('#tl-0').removeClass('btn-primary btn-success').addClass('btn-primary');
    } else if (temporal === 2) {
      toastr.success("Capped SVC temporal layer! (highest FPS)", null, {
        timeOut: 2000
      });
      $('#tl-2').removeClass('btn-primary btn-info btn-success').addClass('btn-success');
      $('#tl-1').removeClass('btn-primary btn-success').addClass('btn-primary');
      $('#tl-0').removeClass('btn-primary btn-success').addClass('btn-primary');
    }
  }

